CREATE USER IF NOT EXISTS 'user'@'%' IDENTIFIED BY 'password';
CREATE DATABASE IF NOT EXISTS db_name;
GRANT ALL PRIVILEGES ON db_name.* TO 'user'@'%';
USE db_name;

CREATE TABlE IF NOT EXISTS mods (
    id VARCHAR(50) NOT NULL,
    name varchar(50) NOT NULL,
    energy_type varchar(10),
    energy_cost int,
    icon_url varchar(200),
    mod_type varchar(30) NOT NULL,
    perk1 varchar(1000),
    perk2 varchar(1000),
    perk3 varchar(1000),
    PRIMARY KEY (id)
);

CREATE TABLE IF NOT EXISTS servers (
    id INT NOT NULL AUTO_INCREMENT,
    server_id VARCHAR(50) NOT NULL,
    info_channel_id VARCHAR(50) NOT NULL,
    control_channel_id VARCHAR(50),
    wishes_channel_id VARCHAR(50),
    control_role_id VARCHAR(50),
    PRIMARY KEY (id)
);

CREATE TABLE IF NOT EXISTS wishes (
    id INT NOT NULL AUTO_INCREMENT,
    user_id VARCHAR(50) NOT NULL,
    mod_id VARCHAR(50) NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (mod_id) REFERENCES mods(id)
);

CREATE TABLE IF NOT EXISTS current_mods (
    id INT NOT NULL AUTO_INCREMENT,
    mod_id VARCHAR(50) NOT NULL,
    vendor VARCHAR(50) NOT NULL,
    PRIMARY KEY (ID),
    FOREIGN KEY (mod_id) REFERENCES mods(id)
);


